import React,{useState} from 'react';
import { FormControl, FormGroup, InputLabel, Input, Typography, Button } from '@mui/material';
import styled from '@emotion/styled';
import axios from 'axios';
import AdminNavBar from './NavBars/AdminNavBar';

const Container = styled(FormGroup)`
    width:50%;
    margin: 5% 30%;
    & > div {
        margin-top:20px;
    }
`

const UploadAdmin = () => {
    const [files, setFiles] = useState([]);
    const submitData = () => {
        const data = new FormData();

        for(let i = 0; i < files.length; i++) {
            data.append('file', files[i]);
        }

        axios.post('http://localhost:8000/single', data)
            .then((response) => {
                alert('Upload Success');
                onSuccess(response.data)
            })
            .catch((e) => {
                alert('Upload Error')
            })

    }

    return(
        <><AdminNavBar /><Container>
            <Typography variant='h4'>Upload Documents</Typography>
            <FormControl>
                <InputLabel>Upload File</InputLabel>
                <Input name="file" type='file' onChange={(e) => { setFiles(e.target.value); } } />
            </FormControl>
            <FormControl>
            <Button variant="contained" onClick={submitData}>Upload</Button>
            </FormControl>
        </Container></>
    )

}

export default UploadAdmin;
